<script>
	import { ViewBox, XHeader, Swiper, Tabbar, TabbarItem,  Drawer, Loading,Toast,Badge,Group,Cell, Grid, GridItem,XDialog} from 'vux'
	import api from '../../components/other/api-url'
	import globalPM from '../../components/other/global-parameter'
	import axios from 'axios'
	import Cookies from 'js-cookie'
	import router from '../../router/index'
	import AvatarCropper from 'vue-avatar-cropper'
	import header from '../../assets/my/myInfo-user-header.png'
	export default {
		components: { ViewBox, XHeader, Swiper, Tabbar, TabbarItem, Drawer, Loading,Toast,Badge ,Group,Cell,Grid, GridItem,AvatarCropper,XDialog},
		data() {
			return {
				isLogin: false,
		        userHeader: header,
		        nickName: '',
		        isBuildingStatus: false,
		        userAvatar: undefined,
		        api: api,
		        uid: localStorage.getItem('uid'),
		        token: localStorage.getItem('token'),
		        globalPM: globalPM,
		        errorStatus: false,
		        errorMsg: '',
		        loading:false,
		        yirenzhen:false,
		        yirenzhena:false,
		        showdialog:false,
			}
		},
		methods: {
			wall(){
				localStorage.setItem('wallet',1)
			},
			help(){
				window.open('https://zechang360.kf5.com/hc/','_self')
			},
			updateUserAvatar (resp) {
	        console.log(resp)
	        if (resp.code === 0) {
	          this.userHeader = resp.data
	          localStorage.setItem('userHeader', resp.data, {expires: 30})
	          this.errorStatus = true
	          this.errorMsg = "头像修改成功"
	        }
	      },
	      avatarUploadBefore (form, xhr) {
	        console.log(form, xhr)
	      },
	      setting(){//设置
	      	let _this = this;
	      	localStorage.setItem('isLogin',_this.isLogin)
	      	router.push('/setting')
	      },
	      vip(){//开通会员
	      	router.push("/vip")
	      },
	      login(){
	      	router.push('/login')
	      },
	      kefu(){
	      	window.open('https://zechang360.kf5.com/kchat?supportbox=1&active_in_iframe=0&lang=zh_CN#/chatbox?robot=1&t='+new Date().getTime(),'_blank')
	      }
		},
		created() {
			localStorage.setItem('yaoqinga',2)
			localStorage.setItem('invite',1)
			localStorage.setItem('wallet',1)
			let _this = this;
			//_this.yirenzhen = localStorage.getItem('yirenzhen')
	        if (localStorage.getItem('token').length > 0 && localStorage.getItem('uid').length > 0) {
	          _this.isLogin = true
	          _this.userHeader = localStorage.getItem('userHeader')
	          _this.nickName = localStorage.getItem('nickName')
	        } else {
	          _this.isLogin = false
	        }
	        //判断是否为会员
	        axios.post(api + '/user/getLevel?' + '&uid=' + localStorage.getItem('uid') + '&token=' + localStorage.getItem('token') + '&valid=0' + globalPM).then(function (res) {
				console.log(res.data)
		        if (res.data.code === 0) {
		        	if(res.data.data.level == 2){
		        		document.getElementById("myheader").style.opacity = 1;
						$('.vipa,.vipz').attr("style","visibility: visible;");
						$('.vip').attr("style","visibility: hidden;");
		        	}else{
		        		document.getElementById("myheader").style.opacity = 0;
						$('.vip').attr("style","visibility: visible;");
						$('.vipa,.vipz').attr("style","visibility: hidden;");
		        	}
		        }
			})
	        axios.post(api + '/user/real/state?' + '&uid=' + localStorage.getItem('uid') + '&token=' + localStorage.getItem('token') + '&valid=0' + globalPM).then(function (res) {
	        	console.log('判断实名认证')
				console.log(res.data)
		        if (res.data.code === 0) {
		        	if(res.data.data.state == 1){
		        		console.log("实名认证了")
		        		_this.yirenzhen = true;
		        		_this.yirenzhena = false;
		        	}else{
		        		console.log("未实名认证")
		        		_this.yirenzhena = true
		        		_this.yirenzhen = false;
		        	}
		        }
		        if (res.data.code === 110) {
			        _this.isLogin = false
			        _this.errorStatus = true
	          		_this.errorMsg = res.data.msg
			    }
			})
		},
	}
</script>

